        {Icon && iconPlacement === "right" && (
          <div className="w-0 translate-x-[100%] pl-0 opacity-0 transition-all duration-200 group-hover:w-5 group-hover:translate-x-0 group-hover:pl-2 group-hover:opacity-100">
            <Icon />
          </div>
        )}
      </Comp>
    );
  },
);
Button.displayName = "Button";

export { Button, buttonVariants };

"use client";

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const inputVariants = cva(
  "flex w-full rounded-md text-sm transition-all file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none disabled:cursor-not-allowed disabled:opacity-50",
  {
    variants: {
      variant: {
        default:
          "border border-input bg-background shadow-sm focus-visible:ring-1 focus-visible:ring-ring",
        underline:
          "border-b border-input bg-transparent px-0 focus-visible:border-primary rounded-none focus-visible:ring-0 focus-visible:outline-none transition-colors duration-200",
        pill: "rounded-full border border-input bg-background px-6 focus-visible:ring-1 focus-visible:ring-ring",
      },
      inputSize: {
        xs: "h-7 px-2 text-xs",
        sm: "h-8 px-3 text-sm",
        md: "h-10 px-4 py-2",
        lg: "h-12 px-6 text-lg",
        xl: "h-14 px-8 text-xl",
      },
    },
    defaultVariants: {
      variant: "default",
      inputSize: "md",
    },
  },
);

interface IconProps {
  Icon: React.ElementType;
  iconPlacement: "left" | "right";
}

interface IconlessProps {
  Icon?: never;
  iconPlacement?: undefined;
}

export interface InputProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, "size">,
    VariantProps<typeof inputVariants> {
  asChild?: boolean;
}

export type InputIconProps = IconProps | IconlessProps;

const Input = React.forwardRef<HTMLInputElement, InputProps & InputIconProps>(
  (
    {
      className,
      variant,
      inputSize,
      asChild = false,
      type,
      Icon,
      iconPlacement,
      ...props
    },
    ref,
  ) => {
    const Comp = asChild ? Slot : "input";

    const inputClassName = cn(
      inputVariants({ variant, inputSize, className }),
      Icon && (iconPlacement === "left" ? "pl-8" : "pr-8"),
    );

    return (
      <div className="relative">
        {Icon && (
          <div
            className={cn(
              "absolute top-1/2 transform -translate-y-1/2 text-muted-foreground pointer-events-none",
              iconPlacement === "left" ? "left-2" : "right-2",
            )}
          >
            <Icon className="w-5 h-5" />
          </div>
        )}
        <Comp type={type} className={inputClassName} ref={ref} {...props} />
      </div>
    );
  },
);
Input.displayName = "Input";

export { Input, inputVariants };

"use client";

import { MoonIcon, SunIcon } from "@radix-ui/react-icons";

import { Button } from "@/components/ui/button";
import React from "react";
import { cn } from "@/lib/utils";
import { useTheme } from "next-themes";

const ModeToggle = React.forwardRef<
  HTMLButtonElement,
  React.ComponentPropsWithoutRef<typeof Button> & { className?: string }
>(({ className, ...props }, ref) => {
  const { theme, setTheme } = useTheme();

  return (
    <Button
      ref={ref}
      variant="outline"
      type="button"
      size="icon"
      className={cn("px-2", className)}
      aria-label="Toggle theme"
      onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
      {...props}
    >
      <SunIcon className="size-[1.2rem] text-neutral-800 dark:hidden dark:text-neutral-200" />
      <MoonIcon className="hidden size-[1.2rem] text-neutral-800 dark:block dark:text-neutral-200" />
    </Button>
  );
});

ModeToggle.displayName = "ModeToggle";

export default ModeToggle;

"use client";

import * as React from "react";
import * as SwitchPrimitives from "@radix-ui/react-switch";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import { Sun, Moon } from "lucide-react";

const switchVariants = cva(
  "peer inline-flex shrink-0 cursor-pointer items-center border-2 border-transparent transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background disabled:cursor-not-allowed disabled:opacity-50 data-[state=checked]:bg-primary data-[state=unchecked]:bg-input",
  {
    variants: {
      variant: {
        default: "rounded",
        rounded: "rounded-full",
        square: "rounded-none",
        ios: "rounded-full data-[state=checked]:bg-green-400",
      },
      size: {
        icon: "h-5 w-5",
        xs: "h-3 w-6",
        sm: "h-4 w-7",
        md: "h-5 w-9",
        lg: "h-6 w-11",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "md",
    },
  },
);

const switchThumbVariants = cva(
  "pointer-events-none block bg-background shadow-lg ring-0 transition-transform data-[state=unchecked]:translate-x-0",
  {
    variants: {
      variant: {
        default: "rounded",
        rounded: "rounded-full",
        square: "rounded-none",
        ios: "rounded-full shadow-md",
      },
      size: {
        icon: "h-4 w-4 data-[state=checked]:translate-x-0",
        xs: "h-2 w-2 data-[state=checked]:translate-x-3",
        sm: "h-3 w-3 data-[state=checked]:translate-x-3",
        md: "h-4 w-4 data-[state=checked]:translate-x-4",
        lg: "h-5 w-5 data-[state=checked]:translate-x-5",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "md",
    },
  },
);

export interface SwitchProps
  extends React.ComponentPropsWithoutRef<typeof SwitchPrimitives.Root>,
    VariantProps<typeof switchVariants> {
  thumbVariant?: VariantProps<typeof switchThumbVariants>["variant"];
  showLabels?: boolean;
  labelOn?: string;
  labelOff?: string;
  showIcons?: boolean;
}

const Switch = React.forwardRef<
  React.ElementRef<typeof SwitchPrimitives.Root>,
  SwitchProps
>(
  (
    {
      className,
      variant,
      size,
      thumbVariant,
      showLabels,
      labelOn = "On",
      labelOff = "Off",
      showIcons,
      checked,
      defaultChecked,
      onCheckedChange,
      ...props
    },
    ref,
  ) => {
    const iconSize = React.useMemo(() => {
      switch (size) {
        case "icon":
          return 12;
        case "xs":
          return 8;
        case "sm":
          return 10;
        case "md":
          return 12;
        case "lg":
          return 14;
        default:
          return 12;
      }
    }, [size]);

    const [internalChecked, setInternalChecked] = React.useState(
      defaultChecked || false,
    );

    const isControlled = checked !== undefined;
    const isChecked = isControlled ? checked : internalChecked;

    const handleCheckedChange = (newChecked: boolean) => {
      if (!isControlled) {
        setInternalChecked(newChecked);
      }
      onCheckedChange?.(newChecked);
    };

    return (
      <div className="inline-flex items-center">
        {showLabels && (
          <span
            className={cn(
              "mr-2 text-sm",
              isChecked ? "text-primary/80" : "text-primary",
            )}
          >
            {labelOff}
          </span>
        )}
        <SwitchPrimitives.Root
          className={cn(switchVariants({ variant, size, className }))}
          checked={isChecked}
          onCheckedChange={handleCheckedChange}
          {...props}
          ref={ref}
        >
          <SwitchPrimitives.Thumb
            className={cn(
              switchThumbVariants({
                variant: thumbVariant || variant,
                size,
              }),
              "flex items-center justify-center",
            )}
          >
            {showIcons &&
              (isChecked ? <Sun size={iconSize} /> : <Moon size={iconSize} />)}
          </SwitchPrimitives.Thumb>
        </SwitchPrimitives.Root>
        {showLabels && (
          <span
            className={cn(
              "ml-2 text-sm",
              isChecked ? "text-primary" : "text-primary/80",
            )}
          >
            {labelOn}
          </span>
        )}
      </div>
    );
  },
);
Switch.displayName = SwitchPrimitives.Root.displayName;

export { Switch };

"use client";

import * as React from "react";
import * as TabsPrimitive from "@radix-ui/react-tabs";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const tabsVariants = cva("", {
  variants: {
    variant: {
      default: "",
      underline: "",
      pill: "",
      rounded: "",
      folder: "",
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

const tabsListVariants = cva(
  "inline-flex items-center justify-center text-muted-foreground",
  {
    variants: {
      variant: {
        default: "bg-muted p-1 rounded-md",
        underline:
          "space-x-6 bg-transparent space-x-4 p-0 border-b rounded-none",
        pill: "bg-transparent p-0 gap-0.5",
        rounded: "bg-muted p-2 rounded-full",
        folder: "space-x-1 border-b",
      },
    },
  },
);

const tabsTriggerVariants = cva(
  "inline-flex items-center justify-center whitespace-nowrap px-3 py-1.5 text-sm font-medium ring-offset-background transition-all focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default:
          "rounded-sm data-[state=active]:bg-background data-[state=inactive]:hover:bg-primary/5 data-[state=active]:text-foreground data-[state=active]:shadow-sm",
        underline:
          "rounded-none data-[state=active]:bg-transparent data-[state=active]:text-foreground data-[state=active]:shadow-none border-b-2 border-transparent data-[state=active]:border-primary data-[state=inactive]:hover:text-foreground",
